<script lang='ts' setup>
import confetti from 'canvas-confetti'

let confettiInstance: any = null
// 用于控制背景烟花的变量
let backgroundAnimationId: number | null = null
let isBackgroundRunning = false

function onClickConfetti(options: confetti.Options) {
  confettiInstance?.(options)
}

function randomInRange(min: number, max: number) {
  return Math.random() * (max - min) + min
}

// 默认效果
function defaultConfetti() {
  onClickConfetti({
    particleCount: 100,
    spread: 70,
    origin: { y: 0.6 },
  })
}

// 主要效果 - 从顶部飘落
function primaryConfetti() {
  const end = Date.now() + 1000
  const colors = ['#409EFF', '#66b1ff', '#8cc5ff'];

  (function frame() {
    confettiInstance({
      particleCount: 3,
      angle: 60,
      spread: 55,
      origin: { x: 0 },
      colors,
    })
    confettiInstance({
      particleCount: 3,
      angle: 120,
      spread: 55,
      origin: { x: 1 },
      colors,
    })

    if (Date.now() < end) {
      requestAnimationFrame(frame)
    }
  }())
}

// 成功效果 - 星星和彩带
function successConfetti() {
  confettiInstance({
    particleCount: 100,
    spread: 70,
    scalar: 0.8,
    origin: { y: 0.6 },
    shapes: ['circle', 'square'],
    colors: ['#67C23A', '#85ce61', '#95d475'],
  })
}

// 信息效果 - 雪花效果
function infoConfetti() {
  confettiInstance({
    particleCount: 150,
    spread: 100,
    scalar: 1.2,
    origin: { y: 0.5 },
    shapes: ['circle'],
    colors: ['#909399', '#a6a9ad', '#b1b3b8'],
    gravity: 0.5,
    drift: 0.1,
  })
}

// 警告效果 - 火焰效果
function warningConfetti() {
  confettiInstance({
    particleCount: 80,
    spread: 60,
    scalar: 1.1,
    origin: { y: 0.7 },
    colors: ['#E6A23C', '#ebb563', '#f0c78a'],
    gravity: 1.2,
    decay: 0.94,
  })
}

// 危险效果 - 爆炸效果
function dangerConfetti() {
  const end = Date.now() + 500
  const colors = ['#F56C6C', '#f78989', '#f89797'];

  (function frame() {
    confettiInstance({
      particleCount: 10,
      angle: randomInRange(55, 125),
      spread: randomInRange(50, 70),
      origin: { y: 0.6 },
      colors,
      gravity: 1.5,
      scalar: 1.2,
    })

    if (Date.now() < end) {
      requestAnimationFrame(frame)
    }
  }())
}

